        </div>

        {/* Main Chat Interface */}
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">
          {/* Conversations List */}
          <Card className="lg:col-span-4 bg-white/90 backdrop-blur-sm border-emerald-200">
