                                  <div className="flex items-center space-x-3">
                                    <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200">
                                      {report.type}
                                    </Badge>
                                    <span className="text-sm text-gray-500">{report.date}</span>
